import { Component } from '@angular/core';

@Component({
  selector: 'app-galeria-imagenes',
  templateUrl: './galeria-imagenes.component.html',
  styleUrls: ['./galeria-imagenes.component.less']
})
export class GaleriaImagenesComponent {
  imagenes: string[] = [
    'assets/imagenes/imagen1.jpg',
    'assets/imagenes/imagen2.jpg',
    'assets/imagenes/imagen3.jpg',
    // Agrega más rutas de imágenes aquí
  ];
  indiceActual: number = 0;

  navegar(direccion: number) {
    this.indiceActual += direccion;
    // Asegurarse de que el índice esté dentro del rango de imágenes disponibles
    if (this.indiceActual < 0) {
      this.indiceActual = this.imagenes.length - 1;
    } else if (this.indiceActual >= this.imagenes.length) {
      this.indiceActual = 0;
    }
  }
}
